defmodule Day01 do
  @starting_floor 0

  def elevate(instructions) when is_binary(instructions) do
    elevate(instructions, @starting_floor, 0)
  end

  # defp elevate(_instructions, -1, count) do
  #   count
  # end

  defp elevate("", current_floor, _count) do
    current_floor
  end

  defp elevate("(" <> rest_instructions, current_floor, count) do
    elevate(rest_instructions, current_floor + 1, count + 1)
  end

  defp elevate(")" <> rest_instructions, current_floor, count) do
    elevate(rest_instructions, current_floor - 1, count + 1)
  end
end

